mapper.select:
select Firewall.*,'不限' as software_name from firewall where user_id=#{id} and software_id=-1  and deleted=false
and (ip like CONCAT('%',#{content},'%') or remark like CONCAT('%',#{content},'%'))
and (software_id=-1 or ip like CONCAT('%',#{content},'%') or remark like CONCAT('%',#{content},'%'))
and state=#{state}
and type=#{type}
and add_time>=#{startTime}
and add_time<=#{endTime}
UNION ALL
select Firewall.*,software_name from firewall,software where firewall.user_id=#{id} and firewall.software_id=software.id  and firewall.deleted=false
and (software_name like CONCAT('%',#{content},'%') or firewall.ip like CONCAT('%',#{content},'%') or firewall.remark like CONCAT('%',#{content},'%'))
and firewall.state=#{state}
and firewall.type=#{type}
and firewall.add_time>=#{startTime}
and firewall.add_time<=#{endTime}
